import React, {useState, useEffect} from 'react';
import axios from 'axios';

const DrfApiFetch = () => {

    const [receipts, setReceipts] = useState([]);
    const [selectedReceipt, setSelectedReceipt] = useState([]);
    const [code, setCode] = useState("");
    const [editedReceipt, setEditedReceipt] = useState({
        code:"", name:"", kana:"", points:0, in_or_out:0})

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/app/ReceiptCodes/",{
            headers: {
                "Authorization": "Token c26528b0fbfe1e7e1b0b58680c1f13b00aabbd7f"
            }
        })
        .then(res => {setReceipts(res.data)})
    }, []);

    const getReceipt = () => {
        axios.get(`http://127.0.0.1:8000/app/ReceiptCodes/${code}`, {
            headers: {
                "Authorization": "Token c26528b0fbfe1e7e1b0b58680c1f13b00aabbd7f"
            }})
            .then(res => {setSelectedReceipt(res.data)
        });
    };

    const deleteReceipt = (code) => {
        axios.delete(`http://127.0.0.1:8000/app/ReceiptCodes/${code}`, {
            headers: {
                "Authorization": "Token c26528b0fbfe1e7e1b0b58680c1f13b00aabbd7f"
            }
        })
        .then(res => {setReceipts(receipts.filter(receipt=>receipt.code !== code)); setSelectedReceipt([])})
    };

    return (
        <div>

            <input className='form-control' type="text" value={code} onChange={evt=>{setCode(evt.target.value)}} />
            <br />
            <button type='button' onClick={()=>getReceipt()}>Get Receipt</button>
            <table className='table'>
                <tbody>
                    <tr>
                        <td scope="col">レセ電コード</td>
                        <td>{selectedReceipt.code}</td>
                    </tr>
                    <tr>
                        <td scope="col">名称</td>
                        <td>{selectedReceipt.name}</td>
                    </tr>
                    <tr>
                        <td scope="col">カナ名称</td>
                        <td>{selectedReceipt.kana}</td>
                    </tr>
                    <tr>
                        <td scope="col">点数</td>
                        <td>{selectedReceipt.points}</td>
                    </tr>
                    <tr>
                        <td scope="col">入外</td>
                        <td>{selectedReceipt.in_or_out}</td>
                    </tr>
                </tbody>
            </table>
            <ul>
                {
                    receipts.map(receipt => <li key={receipt.code}>
                        {receipt.code} {receipt.name}
                        <button onClick={() => deleteReceipt(receipt.code)}>
                            <i className='fas fa-trash-alt'></i>
                        </button>
                    </li>)
                }
            </ul>
        </div>
    )
}

export default DrfApiFetch
