import React, {useState, useEffect} from 'react';
import axios from 'axios';
import ReceiptDetail from './ReceiptDetail';

const DrfApiFetch = () => {

    const [receipts, setReceipts] = useState([]);
    const [selectedReceipt, setSelectedReceipt] = useState([]);
    const [code, setCode] = useState("");
    const [editedReceipt, setEditedReceipt] = useState({
        code:"", name:"", kana:"", points:0, in_or_out:0});
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/app/ReceiptCodes/",{
            headers: {
                "Authorization": "Token c26528b0fbfe1e7e1b0b58680c1f13b00aabbd7f"
            }
        })
        .then(res => {setReceipts(res.data)})
    }, []);

    const getReceipt = () => {
        axios.get(`http://127.0.0.1:8000/app/ReceiptCodes/${code}`, {
            headers: {
                "Authorization": "Token c26528b0fbfe1e7e1b0b58680c1f13b00aabbd7f"
            }})
            .then(res => {setSelectedReceipt(res.data)
        });
    };

    const deleteReceipt = (code) => {
        axios.delete(`http://127.0.0.1:8000/app/ReceiptCodes/${code}`, {
            headers: {
                "Authorization": "Token c26528b0fbfe1e7e1b0b58680c1f13b00aabbd7f"
            }
        })
        .then(res => {setReceipts(receipts.filter(receipt=>receipt.code !== code)); setSelectedReceipt([])})
    };

    const newReceipt = (receipt) => {
        const data = {
            code: receipt.code,
            name: receipt.name,
            kana: receipt.kana,
            points: receipt.points,
            in_or_out: receipt.in_or_out
        }
        axios.post(`http://127.0.0.1:8000/app/ReceiptCodes/`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Token c26528b0fbfe1e7e1b0b58680c1f13b00aabbd7f"
            }})
            .then(res => {setSelectedReceipt(res.data)
        });
    };

    return (
        <div className='container mt-5'>
            <main className="receipt-search">
                <div className='input-group'>
                    <input className='form-control' type="text" value={code} onChange={evt=>{setCode(evt.target.value)}} placeholder="レセ電コードを入力してください"/>
                    <button className='input-group-btn btn btn-primary' type='button' onClick={()=>getReceipt()}>
                        <i className="fa-solid fa-magnifying-glass"></i>
                    </button>
                </div>
                <div className='mt-5'>
                    {selectedReceipt.code && 
                        <ReceiptDetail selectedReceipt={selectedReceipt} />
                    }
                </div>
                <h3 className='mt-5'>項目一覧</h3>
                <table className='table table-bordered'>
                    <thead className="table-light">
                        <tr>
                            <th>レセ電コード</th>
                            <th>名称</th>
                            <th> </th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            receipts.map(receipt => <tr>
                                <td>{receipt.code}</td>
                                <td>{receipt.name}</td>
                                <td> 
                                    <i className='fas fa-trash-alt' onClick={() => deleteReceipt(receipt.code)}></i>
                                </td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </main>

            
            
        </div>
    )
}

export default DrfApiFetch
